script:
frappe.ui.form.on("Sales Order", "refresh", function (frm) {
    if (frm.doc.docstatus == 1) {

        frm.add_custom_button("Mettre à jour et facturer", function(){

            var data1 = {
                cmd_name: frm.doc.name,
                req: frm.doc.demande_associe
            };

            frappe.call({
                method: "autoparts.autoparts.doctype.version_vehicule.version_vehicule.update_cmd_invoice",
                args: data1,
                callback: function (invoice) {

                    frm.reload_doc();
                    console.log(invoice);
                    frappe.set_route("Form","Sales Invoice",invoice.message.name);
                }
            });

        });

        frm.add_custom_button(__("Sync demande"), function () {

            var data1 = {
                cmd_name: frm.doc.name,
                req: frm.doc.demande_associe
            };

            frappe.call({
                method: "autoparts.autoparts.doctype.version_vehicule.version_vehicule.sync_cmd",
                args: data1,
                callback: function (stock_entrise) {
                    console.log("RESULT");
                    console.log(stock_entrise);
                    frm.reload_doc();
                }
            });
        });

    }
});

frappe.ui.form.on("Version vehicule",{
 validate: function(frm){

	var _periode = "("+ frm.doc.date_start + " - " + frm.doc.date_end+")";	 
	frm.set_value('periode' , _periode );
	}
});
